      name: e.target.name.value,
      email: e.target.email.value,
      senha: e.target.password.value,
      questao1: e.target.questao1.value,
      questao2: e.target.questao2.value,
      questao3: e.target.questao3.value,
      questao4: e.target.questao4.value,

    });

    newUser.save().then(() => {
      setUser(newUser);
    });
  };



