                value={info.idade}
                onChange={valorInput}
                />

                <label className="siv">Informe seu Email</label>
                <input 
                type='email'
                name="email"
                placeholder='Email' 
                required
                value={info.email}
                onChange={valorInput}
                />

                <label className="siv">Digite sua Senha</label>
                <input 
                type='password' cols="3" rows="1"
