  name:'',
  idade:'',
  email:'',
  senha:'', 
  questao1:'', 
  questao2:'', 
  questao3:'', 
  questao4:'',
};



axios.post('http://localhost:8000', info, {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
  },
})
