import "./App.css"
import {Box,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState} from "react";
import axios from "axios";
import Buscar from "./buscarPaciente";


function App(){

  
  const url = "http://localhost:8003/questions";
  const [posts, setPosts] = useState('');
  const [selectedQuestion, setSelectedQuestion] = useState([]);
  


  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
  }

  useEffect(() => {
    getUser()
  }, []);

  return(
      <div className="App" >
        <div className="cont">
          <center><h2 >Selecionar Pergunta</h2></center>
            <HStack spacing="4">
              <Box w="100%" ml="15%" mt="5%" >
                <p htmlFor="cpf">Buscar o Cpf do Paciente</p>  
                  <Buscar/>
              </Box>
              <Box w="10%"  mr="15%" mt="5%">
                <p htmlFor="cpf">Cadastrar Paciente</p>
                  <CadastroPaciente/>
                </Box>
            </HStack>
       </div><br></br><br></br>
    <div className="perguntas">
    {posts.length === 0?(
        <p>Carregando...</p>
        ):(  
    posts.map((post) => (
    <div key={post._id}>
        <input
            type="checkbox" 
            id={post._id}
            name={post.perguntas} 
            value={post.perguntas}
            checked={selectedQuestion.includes(post.perguntas)}
            onChange={() => {
              const isChecked = selectedQuestion.includes(post.perguntas);
              setSelectedQuestion(isChecked ? selectedQuestion.filter: [...selectedQuestion, post.perguntas]);
          }}
        />
         {selectedQuestion.length > 0 && (
             <p>(Selected questions: {selectedQuestion.join(", ")})</p>
         )}
            <button onClick={() => {
                    console.log(selectedQuestion);
          
              }}>Use Selected Questions</button>

    </div>
      )))}
     
      </div>
  </div>
  
  )
}
export default App
