import './App.css';
import  {useState} from 'react'
import axios from "axios";
import {
  FormLabel,
  Box,
  Input,
  HStack,
  FormControl,
  Center,

} from "@chakra-ui/react";
import Rad from './button';

const App = () => {
  const [name, setName] = useState('');
  const [idade, setIdade] = useState('');
  const [email, setEmail] = useState('');

  function btnFunction() {
    axios.post('http://localhost:8000/forms', {
      name,
      idade,
      email,

    })
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  }

  return (

    <div>
       <form className="form">
      <h1>Questionario</h1>
      <Center
                as="header"
                h={150}
                bg="pink"
                color="white"
                fontWeight="bold"
                fontSize="4xl"
                pb="8"
              >
                Formul√°rio
              </Center>
      <FormControl display="flex" flexDir="column" gap="4">
      <HStack spacing="4">
      <Box w="100%">
      <FormLabel htmlFor="nome">Nome Completo</FormLabel>
      <Input 
         id="nome" 
         onChange={e => setName(e.target.value)}
         />
      </Box>
      <Box w="100%">
        <FormLabel htmlFor="nasc">Data de Nascimento</FormLabel>
        <Input 
          id="idade" 
          placeholder="idade"
          onChange={e => setIdade(e.target.value)}
        />
      </Box>
      </HStack>
      </FormControl>
      <label className="siv">Informe seu Email</label>
      <input
        type="email"
        placeholder="E-mail"
        onChange={e => setEmail(e.target.value)}
      />



      </form>
      <div>
      <Rad onClick={btnFunction}>Enviar</Rad>

      </div>
    </div>

  );
};

export default App;
