import {FormLabel,Box,Input,HStack,FormControl,
  Center, Modal} from "@chakra-ui/react";
import InputMask from 'react-input-mask';
import React from 'react';
import axios from "axios";
import  {useState} from 'react'
import './cadasPac.css';


const CadastroPaciente = () => {

  const [name, setName] = useState('')
  const [cpfInput, setCpfInput] = useState('');
  const [dataN, setDataN] = useState('');
  const [naturalidade, setNaturalidade] = useState('');
  const [celular, setCelular] = useState('');
  const [telefone, setTelefone] = useState('');
  const [endereço,setEndereço] = useState('');
  const [cidade,setCidade] = useState('');
  const [sexo, setSexo] = useState('Masculino'); 
  const [modalIsOpen,setIsOpen] = useState(false);
  
 


  function handleOpenModal(){
    setIsOpen(true)
  }

  function handleCloseModal(){
    setIsOpen(false)
  }

  function handleCpfChange(event) {
    setCpfInput(event.target.value); 
  }

  function isValidCPF(cpf) {
    const regex = /^\d{3}\.\d{3}\.\d{3}-\d{2}$/;
    return regex.test(cpf);
  }


  function btnFunction() {


   if (isValidCPF(cpfInput)) {
      const cpf = cpfInput.replace(/\D/g, ''); 

    axios.post('http://localhost:8004/Cadastro', {
      _id: cpfInput.replace(/\D/g, ''),
      cpf,name,dataN,naturalidade,celular,
      telefone,endereço,cidade,sexo

    })
      .then(response => {
        console.log(response);
        alert("Paciente Cadastrado com sucesso")
      })
      .catch(error => {
        console.log(error);
      });
    } else {
      alert('Invalid CPF. Please check and try again.');
    }
  }

  return (
    <div className='CadastroPaciente'>
        <button 
          type="submit" 
          className="login-form-btn" 
          onClick={handleOpenModal}>
          Cadastrar
        </button>

    <Modal 
      isOpen={modalIsOpen}
      onRequestClose={handleCloseModal}
      zindex={1999}
      position="absolute"
    >
    <div className="modal-content" zindex={1999}>
      <div className="container-modal">
        <div className="wrap-modal">
          <form>
              <Center
                as="header"
                h={50}
                w={430}
                bg="#f29ef3"
                color="white"
                fontWeight="bold"
                fontSize="4xl"
                pb="8"
              >
              <h2>Cadastro de Paciente</h2>
              </Center><br></br>
            <FormControl display="flex" flexDir="column" gap="4">
                <HStack spacing="4">
                  <Box w="100%">
                    <FormLabel htmlFor="nome">Nome Completo</FormLabel>
                    <Input id="nome" type="nome" 
                    onChange={e => setName(e.target.value)}
                    />
                  </Box>
                  <Box w="100%">
                    <FormLabel htmlFor="cpf">Cpf</FormLabel>
                    <InputMask
                       mask="999.999.999-99"
                       className={cpfInput !== '' ? 'has-val input' : 'input'}
                       type="CPF"
                       onChange={handleCpfChange}
                    /> 
                  </Box>
                </HStack>
                <HStack spacing="4">
                  <Box w="100%">
                    <FormLabel htmlFor="nasc">Data de Nascimento</FormLabel>
                    <Input id="nasc" type="date"
                    onChange={e => setDataN(e.target.value)}
                    />
                  </Box>
                  <Box w="100%">
                    <FormLabel htmlFor="natural">Naturalidade</FormLabel>
                    <Input id="natural" 
                    onChange={e => setNaturalidade(e.target.value)}
                    />
                  </Box>
                </HStack>
                <HStack spacing="4">
                  <Box w="100%">
                    <FormLabel htmlFor="cel">Celular</FormLabel>
                    <Input id="cel" type="number"
                    onChange={e => setCelular(e.target.value)}
                    />
                  </Box>
                  <Box w="100%">
                    <FormLabel htmlFor="Tefone">Telefone</FormLabel>
                    <Input id="Tefone" type="number"
                    onChange={e => setTelefone(e.target.value)}
                    />
                  </Box>
                </HStack>
                <HStack spacing="4">
                  <Box w="100%">
                    <FormLabel htmlFor="endereco">Endereço</FormLabel>
                    <Input id="endereco" 
                    onChange={e => setEndereço(e.target.value)}
                    />
                  </Box>
                  <Box w="100%">
                    <FormLabel htmlFor="cidade">Cidade</FormLabel>
                    <Input id="cidade"
                    onChange={e => setCidade(e.target.value)} 
                    />
                  </Box>
                </HStack>
            </FormControl>
              <div>
                <p>Selecione seu Sexo</p>
                <select value={sexo}  onChange={e => setSexo(e.target.value)} >
                  <option value="Feminino">Feminino</option>
                  <option value="Masculino">Masculino</option>
                </select>
              </div>
            <br></br><br></br>
                <div className="container-login-form-btn">
                  <button type="submit" 
                  className="login-form-btn" 
                  onClick={btnFunction}>Cadastrar</button>
                  <button type="submit" 
                  className="login-form-btn"  
                  onClick={handleCloseModal}>close</button>
                </div>
          </form>
        </div>
      </div>
    </div> 
   
    </Modal>
  </div>
  );
};

export default CadastroPaciente;
