import "./App.css"
import {FormLabel,Box,Input,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState } from "react";
import axios from "axios";


function App(){

  
  const url = "http://localhost:8003/questions";
  const [posts, setPosts] = useState('');


  function buscarPorId() {
    async function buscarPorId() {
      const id = document.getElementById("id-busca").value;
    
      const response = await fetch(`/Cadastro/${id}`);
      const data = await response.json();
    
      if (response.status === 200) {
        const nome = data.name;
        const cpf = data.cpf;
      } else {
        // Apresentar mensagem de erro
      }
    }
}

  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
}

useEffect(()=>{
  getUser()
},[])


    return(
        <div className="App" >
            <div className="cont">
           <center><h2 >Selecionar Pergunta</h2></center>
               <HStack spacing="4">
                  <Box w="100%" ml="15%" mt="5%" >
                    <FormLabel htmlFor="cpf">Buscar o Cpf do Paciente</FormLabel>
                    <Input className="wrap-input" 
                        id="cpf" 
                        type="cpf"
                        placeholder="Digite sua resposta..."
                    />
                  </Box>
                  <Box w="10%"  mr="15%" mt="5%">
                    <FormLabel htmlFor="cpf">Cadastrar Paciente</FormLabel>
                       <CadastroPaciente/>
                  </Box>
                </HStack>
                </div>
              <div>
                  {posts.length ===0?(
                    <p>Carregando...</p>
                  ):(  
                    posts.map((post)=>(
                  <div  key={post._id}>
                    <p className="cen">{post.perguntas}</p>
                  </div>
                     ))
                  )}
              </div>
              <input type="text" id="id-busca" placeholder="Digite o ID"></input>
        </div>
    )
}
export default App
