import  {useState} from 'react'
import './App.css';

import axios from 'axios';


function App() {
  const [message, setMessage] = useState("");

  const [info, setInfo] = useState({
    name: '',
    email: '',
    questao1: '',
    questao2:'',
    questao3:'',
    questao4:'',
    senha: '',
    idade:'',

  });



  const valorInput = e => setInfo({...info, [e.target.name]: e.target.value});
  const sendMsg = async (e) => {
  e.preventDefault();

  const headers = {
    'headers': {
      // Indicar que ser치 enviado os dados em formato de objeto
      'Content-Type': 'application/json'
    }
  };


  await axios.post('http://localhost:8080/message', info, headers)
  .then((response) => { // Acessa o then quando a API retornar status 200
    // Atribuir a mensagem no state message
    setMessage(response.data.message);

    // Limpar os dados do state e os dados dos campos do formul치rio
    setInfo({
      name: '',
      email: '',
      questao1: '',
      questao2:'',
      questao3:'',
      questao4:'',
      senha: '',
      idade:'',
    });
  }).catch((err) => { // Acessa o catch quando a API retornar erro
    // Atribuir a mensagem no state message
    setMessage(err.response.info.message);
  });
}


