import "./App.css"
import {FormLabel,Box,Input,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState,useMemo } from "react";
import axios from "axios";


function App(){

  
  const url = "http://localhost:8003/questions";
  const url2 = "http://localhost:8003/cadastro";

  const [posts, setPosts] = useState('');
  const [cpf,setCpf] = useState('')
  const [busca,setBusca] = useState('')

const cpfFiltrado = useMemo(()=> {
  const lowerBusca = busca.toLowerCase();
  return cpf
  .filter((cpf)=> cpf.toLowerCase().includes(lowerBusca))
},[busca,cpf])

  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
}

useEffect(()=>{
  getUser()
},[])

function getUse(){
  axios.get(url2 )
  .then(response=>{
    const data = response.data 
    setCpf(data)
    console.log(data)
  })
  .catch(error=>console.log(error))
}

useEffect(()=>{
  getUse()
},[])

    return(
        <div className="App" >
            <div className="cont">
           <center><h2 >Selecionar Pergunta</h2></center>
               <HStack spacing="4">
                  <Box w="100%" ml="15%" mt="5%" >
                    <FormLabel htmlFor="cpf">Buscar o Cpf do Paciente</FormLabel>
                    <Input className="wrap-input" 
                        id="cpf" 
                        type="cpf"
                        placeholder="Digite sua resposta..."
                    />
                  </Box>
                  <Box w="10%"  mr="15%" mt="5%">
                    <FormLabel htmlFor="cpf">Cadastrar Paciente</FormLabel>
                       <CadastroPaciente/>
                  </Box>
                </HStack>
                </div>
              <div>
                  {posts.length ===0?(
                    <p>Carregando...</p>
                  ):(  
                    posts.map((post)=>(
                  <div  key={post._id}>
                    <p className="cen">{post.perguntas}</p>
                  </div>
                     ))
                  )}
              </div>
              <div>
                  {cpf.length ===0?(
                    <p>Carregando...</p>
                  ):(  
                    cpf.map((post)=>(
                  <div  key={post._id}>
                    <p className="cen">{post.cpf}</p> 
                  </div>
                     ))
                  )}
              </div>
              <input
              type="text"
              value={busca}
              onChange={(e)=> setBusca(e.target.value)}
              />
          <ul>
            {cpfFiltrado.map((cpf) => (
              <li key={cpf}>{cpf}</li>
            ))}
          </ul>
        </div>
    )
}
export default App
