function App() {
  axios.post('http://localhost:8000', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
  })



const form = document.querySelector("form");

form.addEventListener("submit", async (e) => {
  e.preventDefault();

  const data = {
    nome: form.querySelector("input[name='nome']").value,
    idade: form.querySelector("input[name='idade']").value,

    senha: form.querySelector("input[name='senha']").value,
  };

  // Validação
  if (!data.nome || !data.nome.trim()) {
    throw new Error("O nome não pode ser vazio.");
  }
  if (!data.idade || !data.idade.trim()) {
    throw new Error("A idade não pode ser vazia.");
  }


  if (!data.senha || !data.senha.trim()) {
    throw new Error("A senha não pode ser vazia.");
  }

  await axios.post("/forms", data);
});















