import "./App.css"
import {Box,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState} from "react";
import axios from "axios";

import './CadastroP/cadasPac.css';

function App(){

  
  const url = "http://localhost:8003/questions";
  
  const [posts, setPosts] = useState('');
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  console.log(selectedQuestions)
  const selectedQuestionsRef = useRef([]);

  const handleFinalizar = async () => {
    try {
      if (selectedQuestions.length === 0) {
        // Handle the case where no questions are selected
        alert('Por favor, selecione algumas perguntas antes de finalizar.');
        return;
      }
  
      // Optionally request additional user input:
      const additionalData = prompt('Digite alguma informação adicional (opcional):', '');
  
      const dataToSend = {
        selectedQuestions,
        // Include additionalData if gathered
        ...(additionalData && { additionalData }),
      };
  
      // Replace with your actual API endpoint and method
      const response = await axios.post('http://localhost:8003/send-questions', dataToSend);
  
      if (response.status === 200) {
        // Handle successful submission
        alert('Perguntas enviadas com sucesso!');
        // Optionally clear selected questions or redirect to a confirmation page
      } else {
        // Handle errors from the API
        alert('Falha ao enviar perguntas. Consulte o administrador.');
      }
    } catch (error) {
      // Handle network or other errors
      console.error('Error sending questions:', error);
      alert('Ocorreu um erro ao enviar as perguntas. Tente novamente mais tarde.');
    }
  };
  

  const handlePergunta = (e) => {
    const isChecked = e.target.checked;
    const questionId = e.target.value;
  
    if (isChecked) {
      selectedQuestionsRef.current.push(questionId);
    } else {
      selectedQuestionsRef.current = selectedQuestionsRef.current.filter(
        (id) => id !== questionId
      );
    }
  };
  


  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
  }

  useEffect(() => {
    getUser()
  }, []);

  return(
      <div className="App" >
        <div className="cont">
          <center><h2 >Selecionar Pergunta</h2></center>
            <HStack spacing="4">
              <Box w="100%" ml="15%" mt="5%" >
                
              </Box>
              <Box w="10%"  mr="15%" mt="5%">
                <p htmlFor="cpf">Cadastrar Paciente</p>
                  <CadastroPaciente/>
                </Box>
            </HStack>
       </div><br></br><br></br>
       <div className="perguntas">
        {posts.length === 0 ? (
          <p>Carregando...</p>
        ) : (
          posts.map((post) => (
            <div className="perguntas" key={post._id}>
            <p>{post.perguntas}</p>
            <input 
              type="checkbox"
              id={post._id} 
              value={post._id}
              checked={selectedQuestions.includes(post._id)}
              onChange={handlePergunta}
            />
          </div>
          ))
        )}
      </div>
      <button onClick={handleFinalizar}>Finalizar</button>
  </div>
  )
}
export default App
