import "./App.css"
import {Input} from "@chakra-ui/react";
import React, { useEffect, useState} from "react";
import axios from "axios";

function Buscar(){

    const [posts, setPosts] = useState('');
    //const [filteredPosts, setFilteredPosts] = useState(posts);
    const [cpfInput, setCpfInput] = useState('');
   

    function searchByCpf(cpf) {
        axios.get("http://localhost:8003/cadastro?cpf=" + cpf)
        .then((response) => {
        const data = response.data;
        setPosts(data); 
        })
        .catch((error) => {
        console.error(error);    
        });
    }


    useEffect(() => {
        const filteredPosts = cpfInput
          ? posts.filter((post) => post.cpf === cpfInput)
          : posts;
          console.log(filteredPosts)
      }, [cpfInput, posts]);
    
    return(
        <div>
            <div>
                {posts.length === 0 ? (
                    <p>Carregando...</p>
                ) : posts.length === 1 ? (
                    <div key={posts._id}>
                    <p>CPF: {posts[0].cpf}</p>
                    <p>Nome: {posts[0].name}</p>
                    <p className="cen">{posts.cpf}</p>
                    </div>
                ) : (
                    <p>Nenhum resultado encontrado.</p>
                )}
            </div>
            <Input
            id="cpfInput" 
            value={cpfInput}
            onChange={(e) => {
                setCpfInput(e.target.value);
            }}
            />
            <button 
            onClick={() => {
                searchByCpf(cpfInput);
            }}
            >
            Enter
            </button>        
        </div>

    )
}

export default Buscar
