import "./App.css"
import {FormLabel,Box,Input,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState } from "react";
import axios from "axios";


function App(){

  const urlPerguntas = "http://localhost:8003/questions";
  const urlCpfs = "http://localhost:8003/cadastro";
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchDados();
  }, []);

  function getUser(){
    axios.get("http://localhost:8003/cadastro" )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
}



async function fetchDados() {
  try {
    const [perguntasResponse, cpfsResponse] = await Promise.all([
      axios.get(urlPerguntas),
      axios.get(urlCpfs),
    ]);

    const perguntasData = perguntasResponse.data;
    const cpfsData = cpfsResponse.data;

    const combinedData = perguntasData.map((pergunta, index) => ({
      ...pergunta,
      cpf: cpfsData[index].cpf, // Assumindo que os dados possuem o mesmo índice
    }));

    setPosts(combinedData);
  } catch (error) {
    console.error("Erro ao buscar dados:", error);
  }
}


    return(
        <div className="App" >
            <div className="cont">
           <center><h2 >Selecionar Pergunta</h2></center>
               <HStack spacing="4">
                  <Box w="100%" ml="15%" mt="5%" >
                    <FormLabel htmlFor="cpf">Buscar o Cpf do Paciente</FormLabel>
                    <Input className="wrap-input" 
                        id="cpf" 
                        type="cpf"
                        placeholder="Digite sua resposta..."
                    />
                  </Box>
                  <Box w="10%"  mr="15%" mt="5%">
                    <FormLabel htmlFor="cpf">Cadastrar Paciente</FormLabel>
                       <CadastroPaciente/>
                  </Box>
                </HStack>
                </div>
                <div>
        {posts.length === 0 ? (
          <p>Carregando...</p>
        ) : (
          posts.map((post) => (
            <div key={post._id}>
              <p className="cen">{post.perguntas}</p>
              <p className="cen">{post.cpf}</p>
            </div>
          ))
        )}
      </div>
          
        </div>
    )
}
export default App
