import "./App.css";
//import { FormLabel, Box, Input, HStack } from "@chakra-ui/react";
//import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const urlPerguntas = "http://localhost:8003/questions";
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchDados();
  }, []);

  async function fetchDados() {
    try {
      const perguntasResponse = await axios.get(urlPerguntas);
      const perguntasData = perguntasResponse.data;

      // Obter o _id da URL
      const id = window.location.pathname.split("/")[2];

      // Filtrar os dados da pergunta pelo _id
      const perguntaSelecionada = perguntasData.find((pergunta) => pergunta._id === id);

      // Buscar o CPF do paciente
      const cpfResponse = await axios.get(`${urlCpfs}/${perguntaSelecionada.pacienteId}`);
      const cpfData = cpfResponse.data;

      // Combinar os dados
      const combinedData = {
        ...perguntaSelecionada,
        cpf: cpfData.cpf,
      };

      setPosts([combinedData]);
    } catch (error) {
      console.error("Erro ao buscar dados:", error);
    }
  }

  return (
    <div className="App">
      {/* ... */}
      <div>
        {posts.length === 0 ? (
          <p>Carregando...</p>
        ) : (
          posts.map((post) => (
            <div key={post._id}>
              <p className="cen">{post.perguntas}</p>
              <p className="cen">{post.cpf}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
