import "./App.css"
import {Box,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState} from "react";
import axios from "axios";

import './CadastroP/cadasPac.css';

function App(){

  
  const url = "http://localhost:8003/questions";
  
  const [posts, setPosts] = useState('');
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  console.log(selectedQuestions)



  const handlePergunta = (e) => {
    //const isChecked = e.target.checked;
    //const questionId = e.target.value;
    const perguntas = e.target.value
 
    /*setSelectedQuestions(
      isChecked
        ? [...selectedQuestions, questionId,perguntas]
        : selectedQuestions.filter((id) => id !== questionId,perguntas)
    );
  };*/

  const handleEnviar = async () => {

    const dataToSend = selectedQuestions.map((questionId) => ({
      perguntaId: questionId,perguntas
    }));

    try {
      if (selectedQuestions.length === 0) {
        alert("Por favor, selecione algumas perguntas antes de enviar.");
        return;
      }

      const response = await axios.post("http://localhost:8003/selecao", {selectedQuestions:dataToSend});
     
      console.log(response.data); 
      alert("Perguntas enviadas com sucesso!");

      setSelectedQuestions([]);
    } catch (error) {
      console.error("Error sending questions:", error);
      alert("Ocorreu um erro ao enviar as perguntas. Tente novamente mais tarde.");
    }
  };


  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
  }

  useEffect(() => {
    getUser()
  }, []);

  return(
      <div className="App" >
        <div className="cont">
          <center><h2 >Selecionar Pergunta</h2></center>
            <HStack spacing="4">
              <Box w="100%" ml="15%" mt="5%" >
                
              </Box>
              <Box w="10%"  mr="15%" mt="5%">
                <p htmlFor="cpf">Cadastrar Paciente</p>
                  <CadastroPaciente/>
                </Box>
            </HStack>
       </div><br></br><br></br>
       <div className="perguntas">
        {posts.length === 0 ? (
          <p>Carregando...</p>
        ) : (
          posts.map((post) => (
            <div className="perguntas" key={post._id}>
            <p>{post.perguntas}</p>
            <input 
              type="checkbox"
              id={post._id} 
              value={post._id}
              checked={selectedQuestions.includes(post._id)}
              onChange={handlePergunta}
            />
          </div>
          ))
        )}
      </div>
      <button onClick={handleEnviar}>Enviar Perguntas</button>
  </div>
  )
}
}
export default App
