import "./App.css"
import { Input, Alert } from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { PiMagnifyingGlassLight } from "react-icons/pi";
import { Box } from "@chakra-ui/react";

function Buscar() {
  const [posts, setPosts] = useState([]);
  const [cpfInput, setCpfInput] = useState('');
  const [showError, setShowError] = useState(false);
  const [foundPost, setFoundPost] = useState(null); // Track the found post

  useEffect(() => {
    // Fetch initial posts on component mount
    axios.get("http://localhost:8003/cadastro")
      .then((response) => {
        setPosts(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const searchByCpf = async (cpf) => {
    setShowError(false); // Reset error state before fetching

    try {
      const response = await axios.get("http://localhost:8003/cadastro?cpf=" + cpf);
      const data = response.data;
      setFoundPost(data.length > 0 ? data[0] : null); // Update found post
      setShowError(data.length === 0); // Show error if not found
    } catch (error) {
      console.error(error);
      setShowError(true);
    }
  };

  return (
    <div>
      <p>
        {showError && <Alert status="error">Usuário não encontrado no banco de dados.</Alert>}
      </p>

      <Box w="100%" ml="15%" mt="5%">
        <Input className="wrap-input" id="cpfInput" value={cpfInput} onChange={(e) => setCpfInput(e.target.value)} />
        <PiMagnifyingGlassLight onClick={() => searchByCpf(cpfInput)} />
      </Box>

      {foundPost && (
        <div key={foundPost._id}>
          <p>CPF: {foundPost.cpf}</p>
          <p>Nome: {foundPost.name}</p>
        </div>
      )}
    </div>
  );
}

export default Buscar;

