// store.js
import { createStore } from 'redux';

const initialState = { selectedQuestions: [] };

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_QUESTION':
      return { ...state, selectedQuestions: [...state.selectedQuestions, action.payload] };
    case 'REMOVE_QUESTION':
      return {
        ...state,
        selectedQuestions: state.selectedQuestions.filter((id) => id !== action.payload),
      };
    default:
      return state;
  }
};

const store = createStore(reducer);

export default store;
