import  {useState} from 'react'
import './App.css';

import mongoose from 'mongoose';

const Questionario = mongoose.model('Questionario', {
  name: String,
  idade: Number,
  email: String,
  senha: String,
  questao1: String,
  questao2: String,
  questao3: String,
  questao4: String,
});

function App() {
  const [info, setInfo] = useState({
    name:'',
    idade:'',
    email:'',
    senha:'', 
    questao1:'', 
    questao2:'', 
    questao3:'', 
    questao4:'', 
  });
  const valorInput = e => setInfo({...info, [e.target.name]: e.target.value});

  const handleSignupForm = (e) => {
    e.preventDefault()


    const questionario = new Questionario({
      name: info.name,
      idade: info.idade,
      email: info.email,
      senha: info.senha,
      questao1: info.questao1,
      questao2: info.questao2,
      questao3: info.questao3,
      questao4: info.questao4,
    });

    questionario.save().then(() => {
      console.log('Questionário salvo com sucesso!');
    });
  };


      return(
          <div >
              <form className="form" nSubmit={handleSubmit}>
                <h1>Questionario</h1>
                <label className="siv">Qual é o seu nome?</label>
                <input 
                type='text'

                 placeholder='Nome completo' 

                 value={info.name}
                 onChange={valorInput}
                 />

                <label className="siv">1: Se você pudesse acordar num dia com qualquer nova habilidade ou qualidade, qual seria?</label>
                <input 
                type='text' 
                name="questao1"
                placeholder= 'Respostas'
                required
                value={info.questao1}
                onChange={valorInput}
                />

                <label className="siv">2: Se tivesse um programa de televisão, do que seria?</label>
                <input 
                type='text' 
                name="questao2"
                placeholder= 'Respostas'
                required
                value={info.questao2}
                onChange={valorInput}
                />

                <label className="siv">3: Como seria um dia perfeito para você? (desde o café da manhã até a hora de dormir)</label>
                <input 
                type='text'
                name="questao3"
                 placeholder= 'Respostas'
                 required
                 value={info.questao3}
                 onChange={valorInput}
                 />


                <label className="siv">4: Se você pudesse escolher onde estaria daqui a 5 anos, nesta mesma data, onde estaria?</label>
                <input 
                type='text'
                name="questao4" 
                placeholder= 'Respostas'
                required
                value={info.questao4}
                onChange={valorInput}
                />

                <label className="siv"> Qual é a sua idade?</label>
                <input 
                type='number' 
                name="idade"
                placeholder='Idade '
                required
                value={info.idade}
                onChange={valorInput}
                />

                <label className="siv">Informe seu Email</label>
                <input 
                type='email'
                name="email"
                placeholder='Email' 
                required
                value={info.email}
                onChange={valorInput}
                />

                <label className="siv">Digite sua Senha</label>
                <input 
                type='password' cols="3" rows="1"
                name="senha"
                placeholder='Senha' 
                required
                value={info.senha}
                onChange={valorInput}
                />
                <button type="submit">Enviar</button>
              </form>
          </div>
      )
      } 
export default App;
