useEffect(() => {
    const [posts, setPosts] = useState('');
    const filteredPosts = cpfInput
        ? posts.filter((post) => post.cpf === cpfInput)
        : posts;

    // Render the filtered posts directly within useEffect
    ReactDOM.render(
        <div>
            {filteredPosts.map((post) => (
                <div key={post.id}>{/* Render post details here */}</div>
            ))}
        </div>,
        document.getElementById("posts-container")
    );
}, [cpfInput, posts]);
