import React, { useState, useEffect } from "react";
import axios from "axios";
import { Box, HStack, Input, Alert} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import { PiMagnifyingGlassLight } from "react-icons/pi";

function App() {
  const [posts, setPosts] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [cpfInput, setCpfInput] = useState("");
  const [showError, setShowError] = useState(false);



  /*const fetchPosts = async () => {
    try {
      const response = await axios.get(cpfInput ?
         `http://localhost:8003/cadastro?cpf=${cpfInput}`
       : "http://localhost:8003/questions");
      setPosts(response.data);
    } catch (error) {
      setShowError(true);
      console.error(error);
    }
  };*/

 const url = axios.get(cpfInput ?
   `http://localhost:8003/cadastro?cpf=${cpfInput}` :
  "http://localhost:8003/questions"); 
  function fetchPosts (){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
  }

  useEffect(() => {
    fetchPosts()
  }, []);
 


  const handleSearch = () => {
    setShowError(false);
    fetchPosts();
  };

  const [filteredPosts, setFilteredPosts] = useState([]);
  console.log(filteredPosts)

   useEffect(() => {
     if (Array.isArray(posts)) {
       setFilteredPosts(cpfInput ? posts.filter((post) =>
        post.cpf === cpfInput) : posts);
     }
   }, [cpfInput, posts]);
 

  const handlePergunta = (e) => {
    const isChecked = e.target.checked;
    const questionId = e.target.value;
    setSelectedQuestions(
      isChecked ? [...selectedQuestions, questionId] : selectedQuestions.filter((id) => id !== questionId)
    );
  };

  return (
    <div className="App">
      <div>
        {showError && <Alert status="error">Usuário não encontrado no banco de dados.</Alert>}
        <Input
          className="wrap-input"
          id="cpfInput"
          value={cpfInput}
          onChange={(e) => setCpfInput(e.target.value)}
        />
        <PiMagnifyingGlassLight onClick={handleSearch} />
        {filteredPosts.length > 0 && (
          <div key={filteredPosts[0]._id}>
            <p>CPF: {filteredPosts[0].cpf}</p>
            <p>Nome: {filteredPosts[0].name}</p>
          </div>
        )}
      </div>
      <div className="cont">
        <center>
          <h2>Selecionar Pergunta</h2>
        </center>
        <HStack spacing="4">
          <Box w="100%" ml="15%" mt="5%">
            {/* Add content for this Box */}
          </Box>
          <Box w="10%" mr="15%" mt="5%">
            <p htmlFor="cpf">Cadastrar Paciente</p>
            <CadastroPaciente />
          </Box>
        </HStack>
      </div>
      <br />
      <br />
      <br />
      <div className="perguntas">
        {posts.length === 0 ? (
          <p>Carregando...</p>
        ) : (
          posts.map((post) => (
            <div className="perguntas" key={post._id}>
              <p>{post.perguntas}</p>
              <input
                type="checkbox"
                id={post._id}
                value={post._id}
                checked={selectedQuestions.includes(post._id)}
                onChange={handlePergunta}
              />
            </div>
          ))
        )}
      </div>
      <div className="container-Finalizar-form-btn">
        <button className="Finalizar-form-btn">Finalizar</button>
      </div>
    </div>
  );
}

export default App;
