import "./App.css"
import {FormLabel,Box,Input,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState } from "react";
import axios from "axios";


function App(){

  
  const url = "http://localhost:8003/questions";
  const [posts, setPosts] = useState('');
 
  async function buscarDados() {
    const response = await fetch("/cadastro");
    const data = await response.json();
  
    if (response.status === 200) {
      // Apresentar os dados "data"
    } else  {
      // Apresentar mensagem de erro
    }
  }
  
  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
}

useEffect(()=>{
  getUser()
},[])


    return(
        <div className="App" >
            <div className="cont">
           <center><h2 >Selecionar Pergunta</h2></center>
               <HStack spacing="4">
                  <Box w="100%" ml="15%" mt="5%" >
                    <FormLabel htmlFor="cpf">Buscar o Cpf do Paciente</FormLabel>
                    <Input className="wrap-input" 
                        id="cpf" 
                        type="cpf"
                        placeholder="Digite sua resposta..."
                    />
                  </Box>
                  <Box w="10%"  mr="15%" mt="5%">
                    <FormLabel htmlFor="cpf">Cadastrar Paciente</FormLabel>
                       <CadastroPaciente/>
                  </Box>
                </HStack>
                </div>
              <div>
                  {posts.length ===0?(
                    <p>Carregando...</p>
                  ):(  
                    posts.map((post)=>(
                  <div  key={post._id}>
                    <p className="cen">{post.perguntas}</p>
                  </div>
                     ))
                  )}
              </div>
            <div>
              {listaDePosts = data.map((post) =>{
                     <div key={post._id}>
                     <h2>{post.titulo}</h2>
                     <p>{post.conteudo}</p>
                   </div>
           
              })}
            </div>
        </div>
    )
}
export default App
