import "./App.css";
import { Input, Alert } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { PiMagnifyingGlassLight } from "react-icons/pi";
import { Box } from "@chakra-ui/react";

function Buscar() {
  const [posts, setPosts] = useState([]);
  const [cpfInput, setCpfInput] = useState("");
  const [showError, setShowError] = useState(false);

  async function searchAndSendData() {
    try {
      await searchByCpf(cpfInput); 
      await sendDataToBackend(); 
    } catch (error) {
      console.error(error);
      setShowError(true); 
    }
  }

  function searchByCpf(cpf) {
    axios
      .get("http://localhost:8003/cadastro?cpf=" + cpf)
      .then((response) => {
        const data = response.data;
        setPosts(data);
        setShowError(data.length === 0);
      })
      .catch((error) => {
        console.error(error);
        setShowError(true);
      });
  }

  async function sendDataToBackend() {
    try {
      const response = await axios.post("http://localhost:8003/selecao", {
        filteredPosts: filteredPosts, 
      });

      console.log("Data sent to backend:", response.data);
    } catch (error) {
      console.error("Error sending data:", error);
    }
  }

  const [filteredPosts, setFilteredPosts] = useState([]);

  useEffect(() => {
    if (Array.isArray(posts)) {
      setFilteredPosts(cpfInput ? posts.filter((post) => post.cpf === cpfInput) : posts);
    }
  }, [cpfInput, posts]);

  return (
    <div>
      <p>
        {showError && (
          <Alert status="error">Usuário não encontrado no banco de dados.</Alert>
        )}
      </p>

      <Box w="100%" ml="15%" mt="5%">
        <Input
          className="wrap-input"
          id="cpfInput"
          value={cpfInput}
          onChange={(e) => setCpfInput(e.target.value)}
        />

        <PiMagnifyingGlassLight onClick={searchAndSendData} /> {/* Call the combined function */}

        <div>
          {filteredPosts.length > 0 && (
            <div key={filteredPosts[0]._id}>
              <p>CPF: {filteredPosts[0].cpf}</p>
              <p>Nome: {filteredPosts[0].name}</p>
            </div>
          )}
        </div>
      </Box>
    </div>
  );
}

export default Buscar;
