import "./App.css";
import { Input, Alert } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { PiMagnifyingGlassLight } from "react-icons/pi";
import { Box } from "@chakra-ui/react";

function Buscar() {
  const [cpfInput, setCpfInput] = useState("");
  const [postData, setPostData] = useState(null); // Store found post data
  const [showError, setShowError] = useState(false);

  function searchByCpf(cpf) {
    setShowError(false); // Clear error before search

    axios
      .get(`http://localhost:8003/cadastro?cpf=${cpf}`)
      .then((response) => {
        const data = response.data;
        if (data.length > 0) {
          setPostData(data[0]); // Assume single post, handle array if needed
        } else {
          setShowError(true);
        }
      })
      .catch((error) => {
        console.error(error);
        setShowError(true);
      });
  }

  useEffect(() => {
    // Search for post initially if CPF is present
    if (cpfInput) {
      searchByCpf(cpfInput);
    }
  }, [cpfInput]);
  console.log(cpfInput)

  return (
    <div>
      <p>
        {showError && (
          <Alert status="error">Usuário não encontrado no banco de dados.</Alert>
        )}
      </p>

      <Box w="100%" ml="15%" mt="5%">
        <Input
          className="wrap-input"
          id="cpfInput"
          value={cpfInput}
          onChange={(e) => setCpfInput(e.target.value)}
        />

        <PiMagnifyingGlassLight onClick={() => searchByCpf(cpfInput)} />
      </Box>

      {postData && (
        <div key={postData._id}>
          <p>CPF: {postData.cpf}</p>
          <p>Nome: {postData.name}</p>
          {/* Access and display other relevant information from postData */}
        </div>
      )}

      <button onClick={() => { /* Handle send data using postData */ }}>
        Send Data
      </button>
    </div>
  );
}

export default Buscar;
