import "./App.css"
import {FormLabel,Box,Input,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState} from "react";
import axios from "axios";


function App(){

  
  const url = "http://localhost:8003/questions";
  //const url2 = "http://localhost:8003/cadastro";

  const [posts, setPosts] = useState('');
  const [cpf,setCpf] = useState('')
  const [cpfInput, setCpfInput] = useState('');
  const [filteredPosts, setFilteredPosts] = useState([]);


  function searchByCpf(cpf) {
    axios.get(`http://localhost:8003/cadastro?cpf=${cpf}`)
      .then((response) => {
        const data = response.data;
        setCpf(data); // Update posts with the fetched data
      })
      .catch((error) => {
        console.error(error);
        // Handle error, e.g., display an error message to the user
      });
  }


  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
}

useEffect(() => {
  // Filter posts based on cpfInput, or show all posts if cpfInput is empty
  setFilteredPosts(cpfInput ? cpf.filter((post) => post.cpf === cpfInput) : cpf);
}, [cpf, cpfInput]);
