        <div className="App" >
            <div className="cont">
           <center><h2 >Selecionar Pergunta</h2></center>
               <HStack spacing="4">
                  <Box w="100%" ml="15%" mt="5%" >
                    <FormLabel htmlFor="cpf">Buscar o Cpf do Paciente</FormLabel>
                    <Input className="wrap-input" 
                        id="cpf" 
                        type="cpf"
                        placeholder="Digite sua resposta..."
                    />
                  </Box>
                  <Box w="10%"  mr="15%" mt="5%">
                    <FormLabel htmlFor="cpf">Cadastrar Paciente</FormLabel>
                       <CadastroPaciente/>
                  </Box>
                </HStack>
                </div>
              <div>
                  {posts.length ===0?(
                    <p>Carregando...</p>
                  ):(  
                    posts.map((post)=>(
                  <div  key={post._id}>
                    <p className="cen">{post.perguntas}</p>
                  </div>
                     ))
                  )}
              </div>
              <Input
  // ... other props
  onChange={(e) => {
    setCpfInput(e.target.value);
    searchByCpf(e.target.value); // Call the search function with the input value
  }}
/>

  
           
        </div>
    )
}
export default App
