import './App.css';
import  {useState} from 'react'
import axios from "axios";

const App = () => {
  const [name, setName] = useState('');
  const [idade, setIdade] = useState('');
  const [email, setEmail] = useState('');

  function btnFunction() {
    axios.post('http://localhost:8000/forms', {
      name,
      idade,
      email,

    })
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  }

  return (

    <div>
       <form className="form">
      <h1>Questionario</h1>  <br></br>

      <label className="siv">Qual Ã© o seu nome?</label>
      <input className='placeholder'
        type="text"
        placeholder="Nome"
        onChange={e => setName(e.target.value)}
      />
      <br></br>

      <p >Informe sua Idade?</p>
      <input  className='placeholder'
        type="idade"
        placeholder="idade"
        onChange={e => setIdade(e.target.value)}
      />

      <label className="siv">Informe seu Email</label>
      <input className='placeholder'
        type="email"
        placeholder="E-mail"
        onChange={e => setEmail(e.target.value)}
      />

      <button onClick={btnFunction}>Enviar</button>
      </form>
    </div>
  );
};

export default App;
