import "./App.css"
import {Box,HStack, Input, Alert} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState} from "react";
import axios from "axios";
import './CadastroP/cadasPac.css';
import { PiMagnifyingGlassLight } from "react-icons/pi";


function App(){

  
  const url = "http://localhost:8003/questions";
  
  const [posts, setPosts] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [cpfInput, setCpfInput] = useState('');
  const [showError, setShowError] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(cpfInput ? `http://localhost:8003/cadastro?cpf=${cpfInput}` : 'http://localhost:8003/questions');
      setPosts(response.data);
    } catch (error) {
      setShowError(true);
      console.error(error);
    }
  };
  /*function searchByCpf(cpf) {
    axios.get("http://localhost:8003/cadastro?cpf=" + cpf)
      .then((response) => {
        const data = response.data;
        setPosts(data);
        setShowError(data.length === 0);
      
      })
      .catch((error) => {
        console.error(error);
        setShowError(true);
      });
  }*/
  
  const [filteredPosts, setFilteredPosts] = useState([]);
   console.log(filteredPosts)

    useEffect(() => {
      if (Array.isArray(posts)) {
        setFilteredPosts(cpfInput ? posts.filter((post) =>
         post.cpf === cpfInput) : posts);
      }
    }, [cpfInput, posts]);
  

  const handlePergunta = (e) => {
    
    const isChecked = e.target.checked;
    const questionId = e.target.value;
    setSelectedQuestions(isChecked
      ? [...selectedQuestions, questionId]
      : selectedQuestions.filter((id) => id !== questionId)
    );
  };
  


  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
  }

  useEffect(() => {
    getUser()
  }, []);

  return(
      <div className="App" >
          <div>
            <p>
                 {showError && <Alert status="error"
                 >Usuário não encontrado no banco de dados.</Alert>}
            </p>
             <Input className="wrap-input" 
                id="cpfInput"
                value={cpfInput}
                onChange={(e) => setCpfInput(e.target.value)}
            />
            <PiMagnifyingGlassLight onClick={() => searchByCpf(cpfInput)} />       
        <div>
            {filteredPosts.length > 0 && (
            <div key={filteredPosts[0]._id}>
                <p>CPF: {filteredPosts[0].cpf}</p>
                <p>Nome: {filteredPosts[0].name}</p>
            </div>
            )}     
        </div>
   
        </div>
        <div className="cont">
          <center><h2 >Selecionar Pergunta</h2></center>
            <HStack spacing="4">
              <Box w="100%" ml="15%" mt="5%" >
              
          
              </Box>
              <Box w="10%"  mr="15%" mt="5%">
                <p htmlFor="cpf">Cadastrar Paciente</p>
                  <CadastroPaciente/>
                </Box>
            </HStack>
       </div><br></br><br></br>
       <div className="perguntas">
        {posts.length === 0 ? (
          <p>Carregando...</p>
        ) : (
          posts.map((post) => (
            <div className="perguntas" key={post._id}>
            <p>{post.perguntas}</p>
            <input 
              type="checkbox"
              id={post._id} 
              value={post._id}
              checked={selectedQuestions.includes(post._id)}
              onChange={handlePergunta}
            />
          </div>
          ))
        )}
      </div>
      <div className="container-Finalizar-form-btn">
        <button className="Finalizar-form-btn" 
      >Finalizar</button>
      </div>
  </div>
  )
}
export default App
