import "./App.css"
import {Input} from "@chakra-ui/react";
import React, { useEffect, useState} from "react";
import axios from "axios";

function Buscar(){
    const [posts, setPosts] = useState([]);
    const [cpfInput, setCpfInput] = useState('');
    const [isPatientNotFound, setIsPatientNotFound] = useState(false);
  
    function searchByCpf(cpf) {
        axios.get("http://localhost:8003/cadastro?cpf=" + cpf)
          .then((response) => {
            const data = response.data;
            setPosts(data);
            setIsPatientNotFound(data.length === 0); 
          })
          .catch((error) => {
            console.error(error);
            alert('Cpf nao encontrado')    
          });
      }
    
      const [filteredPosts, setFilteredPosts] = useState([]);

        useEffect(() => {
            if (Array.isArray(posts)) {
            setFilteredPosts(cpfInput ? posts.filter((post) =>
             post.cpf === cpfInput) : posts);
            }
        }, [cpfInput, posts]);
          
    return (
        <div>
             <Input className="wrap-input" 
                id="cpfInput"
                value={cpfInput}
                onChange={(e) => setCpfInput(e.target.value)}
            />
            <button onClick={() => searchByCpf(cpfInput)}>Enter</button>
        <div>
            {filteredPosts.length > 0 && (
            <div key={filteredPosts[0]._id}>
                <p>CPF: {filteredPosts[0].cpf}</p>
                <p>Nome: {filteredPosts[0].name}</p>
            </div>
            )}
             {isPatientNotFound && <p>Paciente n√£o cadastrado.</p>}
        </div>
        
        </div>
    );
}

export default Buscar;
