import "./App.css"
import { Input, Alert } from "@chakra-ui/react";
import React, { useEffect, useState} from "react";
import axios from "axios";
import { PiMagnifyingGlassLight } from "react-icons/pi";

import {Box} from "@chakra-ui/react";

function Buscar() {
    const [posts, setPosts] = useState([]);
    const [cpfInput, setCpfInput] = useState('');
    const [showError, setShowError] = useState(false);
    const [selectedQuestions, setSelectedQuestions] = useState([]);

    function searchByCpf(cpf) {
      axios.get("http://localhost:8003/cadastro?cpf=" + cpf)
        .then((response) => {
          const data = response.data;
          setPosts(data);
          setShowError(data.length === 0);
        
        })
        .catch((error) => {
          console.error(error);
          setShowError(true);
        });
    }
    
    async function sendDataToBackend() {
      try {
        const response = await axios.post('http://localhost:8003/selecao', {
          _id: filteredPosts[0]._id, // Send the _id
          filteredPosts // Include filteredPosts
        });
    
        console.log('Data sent to backend:', response.data);
      } catch (error) {
        console.error('Error sending data:', error);
      }
    }
    
    const handleEnviar = async () => {
      const dataToSend = [{
        _id: filteredPosts[0]._id, // Include the _id
        selectedQuestions: selectedQuestions.map((questionId) => ({
          perguntaId: questionId,
          pergunta: posts.find((post) => post._id === questionId).perguntas,
        }))
      }];
    
      // ... rest of the function remains the same
    };
    const [filteredPosts, setFilteredPosts] = useState([]);
     console.log(filteredPosts)

      useEffect(() => {
        if (Array.isArray(posts)) {
          setFilteredPosts(cpfInput ? posts.filter((post) =>
           post.cpf === cpfInput) : posts);
        }
      }, [cpfInput, posts]);
    
    return (
        <div>
            <p>
                 {showError && <Alert status="error"
                 >Usuário não encontrado no banco de dados.</Alert>}
            </p>
         
            <Box w="100%" ml="15%" mt="5%" >
                
             <Input className="wrap-input" 
                id="cpfInput"
                value={cpfInput}
                onChange={(e) => setCpfInput(e.target.value)}
            />
                 
            <PiMagnifyingGlassLight onClick={() => searchByCpf(cpfInput)} />       
        <div>
            {filteredPosts.length > 0 && (
            <div key={filteredPosts[0]._id}>
                <p>CPF: {filteredPosts[0].cpf}</p>
                <p>Nome: {filteredPosts[0].name}</p>
            </div>
            )}    
      
        </div>
                </Box>            
       <button onClick={handleEnviar}>Enviar</button>
                <button onClick={sendDataToBackend}>Send Data</button>
        </div>
    );
}

export default Buscar;
