import{useState} from 'react'
import './App.css';
import Express from 'Express';

const app = Express(email,idade);

app.post('/respostas', (req, res) =>{
return res.json ([email,idade])
});

app.listen(3000, () => 
console.log('Servidor iniciado na porta 3000')
);


function App() {

const[name,setName] = useState('')
const[idade,setIdade] = useState('')
const[email,setEmail] = useState('')
const[senha,setSenha] = useState('')
const[questao1,setQuestao1] = useState('')
const[questao2,setQuestao2] = useState('')
const[questao3,setQuestao3] = useState('')
const[questao4,setQuestao4] = useState('')

  const handleSignupForm = (e) => {
    e.preventDefault()
  console.log({name,questao1,questao2,questao3,questao4,idade,senha,email})
  }
      return(
          <div >
              <form className="form" onSubmit={handleSignupForm}>
                <h1>Questionario</h1>
                <label className="siv">Qual é o seu nome?</label>
                <input 
                type='text'
                 placeholder='Nome completo ' 
                 required
                 value={name}
                 onChange={(e)=>setName(e.target.value)}
                 />

                <label className="siv">1: Se você pudesse acordar num dia com qualquer nova habilidade ou qualidade, qual seria?</label>
                <input 
                type='text' 
                placeholder= 'Respostas'
                required
                value={questao1}
                onChange={(e)=>setQuestao1(e.target.value)}
                />

                <label className="siv">2: Se tivesse um programa de televisão, do que seria?</label>
                <input type='text' 
                placeholder= 'Respostas'
                required
                value={questao2}
                onChange={(e)=>setQuestao2(e.target.value)}
                />

                <label className="siv">3: Como seria um dia perfeito para você? (desde o café da manhã até a hora de dormir)</label>
                <input type='text'
                 placeholder= 'Respostas'
                 required
                 value={questao3}
                onChange={(e)=>setQuestao3(e.target.value)}
                 />


                <label className="siv">4: Se você pudesse escolher onde estaria daqui a 5 anos, nesta mesma data, onde estaria?</label>
                <input type='text' 
                placeholder= 'Respostas'
                required
                value={questao4}
                onChange={(e)=>setQuestao4(e.target.value)}
                />

                <label className="siv"> Qual é a sua idade?</label>
                <input type='number' 
                placeholder='Idade '
                required
                value={idade}
                onChange={(e)=>setIdade(e.target.value)}
                />

                <label className="siv">Informe seu Email</label>
                <input 
                type='email'
                placeholder='Email' 
                required
                value={email}
                onChange={(e)=>setEmail(e.target.value)}
                />

                <label className="siv">Digite sua Senha</label>
                <input 
                type='password' 
                placeholder='Senha' 
                required
                value={senha}
                onChange={(e)=>setSenha(e.target.value)}
                />
                <button type='submit'>Enviar</button>
              </form>
          </div>
      )
  }

export default App;
