import "./App.css"
import {FormLabel,Box,Input,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState} from "react";
import axios from "axios";


function App(){

  
  //const url = "http://localhost:8003/questions";


 // const [posts, setPosts] = useState('');
  const [cpfInput, setCpfInput] = useState('');
  const [busca,setBusca] = useState('')
  
 const cpfFiltradas = cpfInput
      .filter((cpf)=> cpf.startsWith(busca));

  function searchByCpf(cpf) {
    axios.get("http://localhost:8003/cadastro?cpf=" + cpf)
      .then((response) => {
        const data = response.data;
        setCpfInput(data); 
      })
      .catch((error) => {
        console.error(error);    
      });
  }

  useEffect(() => {
    if (cpfInput) {
      searchByCpf(cpfInput);
    }
  }, [cpfInput]);


 /*useEffect(() => {
    const filteredPosts = cpfInput
      ? posts.filter((post) => post.cpf === cpfInput)
      : posts;
      console.log(filteredPosts)
  }, [cpfInput, posts]);*/


 /* function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
  }

  useEffect(() => {
    getUser()
  }, []);*/

  return(
      <div className="App" >
        <div className="cont">
          <center><h2 >Selecionar Pergunta</h2></center>
            <HStack spacing="4">
              <Box w="100%" ml="15%" mt="5%" >
                <FormLabel htmlFor="cpf">Buscar o Cpf do Paciente</FormLabel>
                  <Input className="wrap-input" 
                    id="cpf" 
                    type="cpf"
                    placeholder="Digite sua resposta..."
                  />
              </Box>
              <Box w="10%"  mr="15%" mt="5%">
                <FormLabel htmlFor="cpf">Cadastrar Paciente</FormLabel>
                  <CadastroPaciente/>
                </Box>
            </HStack>
       </div>
      
  
    <Input
      id="cpfInput" 
      value={cpfInput}
      onChange={(e) => {
        setCpfInput(e.target.value);
      }}
    />
    <button 
      onClick={() => {
        searchByCpf(cpfInput);
      }}
    >
      Enter
    </button>   
    <button 
      onClick={setBusca}
    >
      Enter
    </button>  
    <div>
        {cpfInput.length === 0 ? (
          <p>Carregando...</p>
        ) : cpfInput.length > 0 && (
          <div>
            {cpfInput.map((post) => (
              <div key={post._id}>
                <p>CPF: {post.cpf}</p>
                <p>Nome: {post.name}</p>
              </div>
            ))}
          </div>
        )}
      </div>
      <ul>
        {cpfFiltradas.map((cpf)=>(
          <li key={cpf}> {cpf}</li>
        ))}
      </ul>
      </div>
  )
}
export default App
