import "./App.css"
import { Input, Alert } from "@chakra-ui/react";
import React, { useEffect, useState} from "react";
import axios from "axios";
import { PiMagnifyingGlassLight } from "react-icons/pi";

import {Box} from "@chakra-ui/react";
import { useSelector, useDispatch } from 'react-redux';
import { addQuestion, removeQuestion } from './actions';



function Buscar() {
    const [posts, setPosts] = useState([]);
    const [cpfInput, setCpfInput] = useState('');
    const [showError, setShowError] = useState(false);
    const dispatch = useDispatch();
    const selectedQuestions = useSelector((state) => state.selectedQuestions);
  
    const handlePergunta = (e) => {
      const isChecked = e.target.checked;
      const questionId = e.target.value;
  
      if (isChecked) {
        dispatch(addQuestion(questionId));
      } else {
        dispatch(removeQuestion(questionId));
      }
    };
  
    const handleFinalizar = async () => {
   
      const dataToSend = { }
  
      const response = await axios.post('http://localhost:8003/send-questions', dataToSend);

    };
  



    function searchByCpf(cpf) {
      axios.get("http://localhost:8003/cadastro?cpf=" + cpf)
        .then((response) => {
          const data = response.data;
          setPosts(data);
          setShowError(data.length === 0);
        
        })
        .catch((error) => {
          console.error(error);
          setShowError(true);
        });
    }
    
    const [filteredPosts, setFilteredPosts] = useState([]);
     console.log(filteredPosts)

      useEffect(() => {
        if (Array.isArray(posts)) {
          setFilteredPosts(cpfInput ? posts.filter((post) =>
           post.cpf === cpfInput) : posts);
        }
      }, [cpfInput, posts]);
    
    return (
        <div>
            <p>
                 {showError && <Alert status="error"
                 >Usuário não encontrado no banco de dados.</Alert>}
            </p>
         
            <Box w="100%" ml="15%" mt="5%" >
                
             <Input className="wrap-input" 
                id="cpfInput"
                value={cpfInput}
                onChange={(e) => setCpfInput(e.target.value)}
            />
                 
            <PiMagnifyingGlassLight onClick={() => searchByCpf(cpfInput)} />       
        <div>
            {filteredPosts.length > 0 && (
            <div key={filteredPosts[0]._id}>
                <p>CPF: {filteredPosts[0].cpf}</p>
                <p>Nome: {filteredPosts[0].name}</p>
            </div>
            )}    
      
        </div>
                </Box>            
     
           <div className="container-Finalizar-form-btn">
                <button className="Finalizar-form-btn" 
            onClick={handleFinalizar}  >Finalizar</button>
          </div>

        </div>
    );
}

export default Buscar;
