import "./App.css";
import { Input, Alert } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { PiMagnifyingGlassLight } from "react-icons/pi";
import { Box } from "@chakra-ui/react";

function Buscar() {
  const [posts, setPosts] = useState([]);
  const [cpfInput, setCpfInput] = useState("");
  const [showError, setShowError] = useState(false);

  function searchByCpf(cpf) {
    axios
      .get("http://localhost:8003/cadastro?cpf=" + cpf)
      .then((response) => {
        const data = response.data;
        setPosts(data); // Directly update posts with fetched data
        setShowError(data.length === 0);
      })
      .catch((error) => {
        console.error(error);
        setShowError(true);
      });
  }

  async function sendDataToBackend() {
    try {
      const response = await axios.post("http://localhost:8003/selecao", {
        // Access and send the found post directly
        post: posts[0], // Assuming a single post is returned
      });

      console.log("Data sent to backend:", response.data);
    } catch (error) {
      console.error("Error sending data:", error);
    }
  }

  useEffect(() => {
    // Search for posts initially
    searchByCpf(cpfInput);
  }, [cpfInput]);

  return (
    <div>
      <p>
        {showError && (
          <Alert status="error">Usuário não encontrado no banco de dados.</Alert>
        )}
      </p>

      <Box w="100%" ml="15%" mt="5%">
        <Input
          className="wrap-input"
          id="cpfInput"
          value={cpfInput}
          onChange={(e) => setCpfInput(e.target.value)}
        />

        <PiMagnifyingGlassLight onClick={() => searchByCpf(cpfInput)} />
      </Box>

      {posts.length > 0 && (
        <div key={posts[0]._id}>
          <p>CPF: {posts[0].cpf}</p>
          <p>Nome: {posts[0].name}</p>
        </div>
      )}

      <button onClick={sendDataToBackend}>Send Data</button>
    </div>
  );
}

export default Buscar;
