import "./App.css"
import {Box,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState} from "react";
import axios from "axios";
import Buscar from "./buscarPaciente";
import './CadastroP/cadasPac.css';

function App(){

  
  const url = "http://localhost:8003/questions";

  const [posts, setPosts] = useState('');
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  console.log(selectedQuestions)


  const handlePergunta = (e) => {
    
    const isChecked = e.target.checked;
    const questionId = e.target.value;
    setSelectedQuestions(isChecked
      ? [...selectedQuestions, questionId]
      : selectedQuestions.filter((id) => id !== questionId)
    );
  };
  
  function btnFunction() {

     axios.post('http://localhost:8003/finalizar', {
      selectedQuestions,posts
 
     })
       .then(response => {
         console.log(response);
       })
       .catch(error => {
         console.log(error);
       });
  
   }



  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
  }

  useEffect(() => {
    getUser()
  }, []);

  return(
      <div className="App" >
        <div className="cont">
          <center><h2 >Selecionar Pergunta</h2></center>
            <HStack spacing="4">
              <Box w="100%" ml="15%" mt="5%" >
                <p htmlFor="cpf">Buscar o Cpf do Paciente</p>  
                  <Buscar/>
              </Box>
              <Box w="10%"  mr="15%" mt="5%">
                <p htmlFor="cpf">Cadastrar Paciente</p>
                  <CadastroPaciente/>
                </Box>
            </HStack>
       </div><br></br><br></br>
       <div className="perguntas">
        {posts.length === 0 ? (
          <p>Carregando...</p>
        ) : (
          posts.map((post) => (
            <div className="perguntas" key={post._id}>
            <p>{post.perguntas}</p>
            <input 
              type="checkbox"
              id={post._id} 
              value={post._id}
              checked={selectedQuestions.includes(post._id)}
              onChange={handlePergunta}
            />
          </div>
          ))
        )}
      </div>
      <div className="container-Finalizar-form-btn">
        <button className="Finalizar-form-btn" 
       onClick={btnFunction}>Finalizar</button>
      </div>
  </div>
  )
}
export default App
