import './App.css';




function App() {

  const [info, setInfo] = useState({
    name: '',
    email: '',
    questao1: '',
    questao2:'',
    questao3:'',
    questao4:'',
    senha: '',
    idade:''
  });

  const valorInput = e => setInfo({...info, [e.target.name]: e.target.value});




