import "./App.css"
import {FormLabel,Box,Input,HStack} from "@chakra-ui/react";
import CadastroPaciente from "./CadastroP/cadasPaciente";
import React, { useEffect, useState } from "react";
import axios from "axios";


function App(){

  
  const url = "http://localhost:8003/questions";
 const url2 = "http://localhost:8003/cadastro";


  const [cpfPesquisado, setCpfPesquisado] = useState("");
  const [cpfEncontrado, setCpfEncontrado] = useState(null);
  const [posts, setPosts] = useState('');
  //const [cpf,setCpf] = useState('')
  
  function getUser(){
    axios.get(url )
    .then(response=>{
      const data = response.data 
      setPosts(data)
      console.log(data)
    })
    .catch(error=>console.log(error))
}

useEffect(()=>{
  getUser()
},[])

useEffect(() => {
  const handleInputChange = (event) => {
    setCpfPesquisado(event.target.value);
  };

  const inputElement = document.getElementById("cpf");
  inputElement.addEventListener("change", handleInputChange);

  return () => {
    inputElement.removeEventListener("change", handleInputChange);
  };
}, []);

useEffect(() => {
  if (cpfPesquisado) {
    fetchCpf();
  }
}, [cpfPesquisado]);

async function fetchCpf() {
  try {
    const response = await axios.get(`${url2}/${cpfPesquisado}`);
    const cpfData = response.data;
    setCpfEncontrado(cpfData);
  } catch (error) {
    console.error("Erro ao buscar CPF:", error);
    setCpfEncontrado(null);
  }
}

