import  {useState} from 'react'
import './App.css';


function App() {

  const [info, setInfo] = useState({
    name :'',
    idade:'',
    email:'',
    senha:'', 
    questao1:'', 
    questao2:'', 
    questao3:'', 
    questao4:'', 
  });
  const valorInput = e => setData({...info, [e.target.name]: e.target.value});


